sourcesContent:
import { Router } from "express";
const router = Router();

import * as userCtrl from '../controllers/user.controller';
import {authJwt, verifySignup} from '../middlewares'


router.post(
  "/",
  [authJwt.verifyToken, authJwt.checkingRoles, verifySignup.checkRolesExisted],
  userCtrl.createUser
);

router.get("/", userCtrl.getUsers);// yes

router.get("/:userId", userCtrl.getUserById);// yes

router.put("/:userId",[ authJwt.verifyToken, verifySignup.checkRolesExisted], userCtrl.updateUserById);

router.patch("/:userId", [ authJwt.verifyToken, verifySignup.checkRolesExisted], userCtrl.deleteUsuario);// yes

router.delete('/:userId', [ authJwt.verifyToken, authJwt.checkingRoles, verifySignup.checkRolesExisted], userCtrl.destroyUser);//yes
 

export default router;

