sourcesContent:
import { Router } from "express";
const router = Router();

import * as productsCtrl from '../controllers/products.controller';
import {authJwt} from '../middlewares';


router.post("/", [authJwt.verifyToken, authJwt.checkingAllsRoles], productsCtrl.createProduct);
router.get("/", productsCtrl.getProducts);
router.get("/:productId",  productsCtrl.getProductById);
router.put("/:productId", [authJwt.verifyToken, authJwt.isAdmin], productsCtrl.updateProductById);
router.delete("/:productId", [authJwt.verifyToken, authJwt.isAdmin], productsCtrl.deleteProductById);
// 
router.delete("/:productUserId", productsCtrl.deleteProductByUserId);




export default router;

